import React, { useState } from "react";
import menuContext from "./menuContext";

const MenuState = (props) => {
    const host = "http://localhost:5000"
    const menuDataInitial = []
    const [menu, setMenu] = useState(menuDataInitial)


    //   Add a menu
    const addMenu = async (name, category, menus, description) => {
        const response = await fetch(`${host}/api/menuData/addmenu`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },

            body: JSON.stringify({ name, category, menus, description}) // body data type must match "Content-Type" header
        });

        const menu = await response.json();
        setMenu(menu.concat(menu))
    }

   
    return (
        <menuContext.Provider value={{menu, addMenu }}>
            {props.children}
        </menuContext.Provider>
    )
}

export default MenuState;
