import React, { useState, useEffect } from 'react'
import "./styles.css"

const Menu = () => {
    const [menuData, setMenuData] = useState([]);
    const [formData, setFormData] = useState({
        category: '',
        menus: '',
        description: '',
    });

    useEffect(() => {
        fetch('/api/createmenuData')
            .then((response) => response.json())
            .then((data) => {
                setMenuData(data); // Assuming your API returns an object with category, menu, and description properties
            })
            .catch((error) => {
                console.error('Error fetching menu data:', error);
            });
    }, []);

    // Function to update form data when inputs change
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };
    
    const handleSaveChanges = () => {
        // Create a new item with the form data
        const newItem = {
            category: formData.category,
            menus: formData.menus,
            description: formData.description,
        };
    
        // Add the new item to the menuData state
        setMenuData([...menuData, newItem]);
    
        // Clear the form inputs
        setFormData({
            category: '',
            menu: '',
            description: '',
        });
    
        // Close the modal (you can add this logic if needed)
        // document.getElementById('exampleModal').modal('hide');
    };
    
    return (
        <div className="container mt-3" style={{ display: "grid" }}>
            <button type="button" className="btn btn-info ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Add New
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered modal-lg">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Add New Category</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="dropdown">
                                <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false"
                                    style={{ marginBottom: '5px', marginLeft: '0px' }}>
                                    Category
                                </button>
                                <ul className="dropdown-menu">
                                    <li><a className="dropdown-item" href="/">Chinese</a></li>
                                    <li><a className="dropdown-item" href="/">Italian</a></li>
                                    <li><a className="dropdown-item" href="/">Indian</a></li>
                                    <li><a className="dropdown-item" href="/">Beverages</a></li>
                                </ul>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="exampleFormControlInput1" className="form-label">Menus</label>
                                <input type="text" className="form-control" id="exampleFormControlInput1" value={formData.menus}
                                    onChange={handleInputChange} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="exampleFormControlTextarea1" className="form-label">Description</label>
                                <textarea className="form-control" id="exampleFormControlTextarea1" rows="3"
                                 value={formData.description}
                                 onChange={handleInputChange}></textarea>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="formFile" className="form-label">Select a File</label>
                                <input className="form-control" type="file" id="formFile" />
                            </div>
                        </div>
                        <div className="modal-footer">
                            {/* <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button> */}
                            <button type="button" className="btn btn-primary" onClick={handleSaveChanges}>Save changes</button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="row">
            {menuData.map((item, index) => {
                return (
                <><div className="col-md-4"  key={index}>
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Card 1</h5>
                            <div className="mb-3">
                                <label htmlFor="exampleFormControlInput1" className="form-label">Name</label>
                                <input type="text" className="form-control" id="exampleFormControlInput1" />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="exampleFormControlTextarea1" className="form-label">{item.category}</label>
                                <input type="category" className="form-control" id="exampleFormControlInput1" />
                            </div>
                        </div>
                    </div>
                </div><div className="col-md-4">
                        <div className="card" style={{ width: "18rem" }}>
                            <img src="https://img.freepik.com/free-vector/organic-flat-rustic-restaurant-menu-template-with-photo_52683-62703.jpg?w=2000"
                                className="card-img-top" alt="..." />
                            <div className="card-body">
                                <p className="card-text">{item.menus}</p>
                            </div>
                        </div>
                    </div></>
                );
                })}
                {/* <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Card 3</h5>
                            <p className="card-text">This is the content of card 3.</p>
                        </div>
                    </div>
                </div> */}
            </div>
        </div>
    )
}

export default Menu
