import React, { useState } from "react";
import menuContext from "./menuContext";

const MenuState = (props) => {
    const host = "http://localhost:5000";
    const menuDataInitial = [];
    const [menuData, setMenuData] = useState(menuDataInitial);
    const [fileData] = useState(); 

    const getMenu = async () => {

        // API CALL
        const response = await fetch(`${host}/api/menuData/fetchmenudata`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },

        });
        const json = await response.json()
        setMenuData(json);
    }

    // Add a menu    
    const addMenu = async (name, category, price, description, file) => {
        try {
            const data = new FormData();
            data.append('image', fileData)
            const response = await fetch(`${host}/api/menuData/addmenu/single`, {
                method: "POST",
                body: data,
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ name, category, price, description, file })
            });

            if (!response.ok) {
                // Handle HTTP error status codes
                const errorMessage = await response.text();
                console.error("HTTP Error:", errorMessage);
                // Handle the error as needed.
            } else {
                // Parse the JSON response when successful
                const responseData = await response.json();
                console.log(responseData.message); // You can display a success message if needed
                setMenuData([...menuData, responseData.data]);
            }
        } catch (error) {
            // Handle network errors or exceptions
            console.error("Network error:", error);
            // Handle the error as needed.
        }
    };

    // Delete a note
    const deletemenu = async (name) => {
        // API Call
        const response = await fetch(`${host}/api/menuData/deletemenu`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ name }),
        });

        const json = response.json(); // parses JSON response into native JavaScript objects
        console.log(json);

        console.log("Deleting the menu with name " + name);

        const newMenu = menuData.filter((menu) => menu.name !== name);
        setMenuData(newMenu);
    };

    // Edit a note
    const editMenu = async (name, category, price, description, file) => {
        // API Call
        const response = await fetch(`${host}/api/menuData/updatemenu`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },

            body: JSON.stringify({ name, category, price, description, file }) // body data type must match "Content-Type" header
        });
        const json = await response.json(); // parses JSON response into native JavaScript objects
        console.log(json);

        // Logic to edit in client
        const existingMenu = menuData.map((menu) => {
            if (menu.name === name) {
                return {
                    name,
                    category,
                    price,
                    description,
                    file,
                };
            }
            return menu;
        });
        setMenuData(existingMenu);
    }

    return (
        <menuContext.Provider value={{ menuData, addMenu, getMenu, editMenu, deletemenu }}>
            {props.children}
        </menuContext.Provider>
    );
};

export default MenuState;
