        'Sarovar Portico': {
            items: [
                { name: 'Butter Naan', price: 50, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRGk-LKmmf8Do_eFTsaqOrU3VFD2X87DgDjvw&usqp=CAU' },
                { name: 'Mix Veg', price: 110, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUD95rTk5BhESm4y557G2JncSpHhljGADPzQ&usqp=CAU' },
                { name: 'Pizza', price: 350, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr3UQ1WyKU87GaXiXozpFo5mc-tqvANrDGTw&usqp=CAU' },
                { name: 'Chowmein', price: 70, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRKN7E5nNju7MFg0AuJ4qVqCAar1KBb3kLhoA&usqp=CAU' },
                { name: 'Juice', price: 100, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT_ta1ybNoGNht6z0FnuRrKMPPDBgCD3jp4AQ&usqp=CAU' },
                { name: 'Salad', price: 65, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQgrpIA2W9ugcf0b6-n70IDRax0_ln3ChoCtg&usqp=CAU' },
            ],
        },
