            ],
        }
    };

    const addToCart = (item) => {
        // Check if the item is already in the cart
        const itemIndex = cart.findIndex((cartItem) => cartItem.name === item.name);

        if (itemIndex !== -1) {
            // If it's already in the cart, update the quantity
            const updatedCart = [...cart];
            updatedCart[itemIndex].quantity += 1;
            setCart(updatedCart);
        } else {
            // If it's not in the cart, add it with quantity 1
            setCart([...cart, { ...item, quantity: 1 }]);
        }
    };

    // Function to open the modal and set the selected item
    const openModal = (item) => {
        setSelectedItem({ ...item, quantity: 0 });
    };

    // Function to close the modal
    const closeModal = () => {
        setSelectedItem(null);
    };

    // Function to update quantity in the modal
    const updateQuantity = (value) => {
        setSelectedItem({ ...selectedItem, quantity: selectedItem.quantity + value });
    };

    const handleDeliveryChange = (event) => {
        setSelectedDeliveryCharge(parseInt(event.target.value, 10));
    };

    // Calculate total including the selected delivery charge
    const total = selectedItem ? selectedItem.price * selectedItem.quantity + selectedDeliveryCharge : 0;

    return (
        <div className="container">
            <h2 className="pb-3 fs-4">{restaurantName}</h2>
            <div className="row row-cols-1 row-cols-md-3 g-4" style={{ marginBottom: '20px' }}>
                {restaurants[restaurantName].items.map((item, index) => (
                    <div key={index} className="col">
                        <div className="card">
                            <img src={item.imageUrl} alt={item.name} className="card-img-top" style={{ height: '200px' }} />
                            <div className="card-body">
                                <h5 className="card-title">{item.name}</h5>
                                <p className="card-text">&#8377;{item.price}</p>
                                <div className="card-button" style={{ position: 'absolute', bottom: '25px', right: '10px' }}>
                                    <button
                                        type="button"
                                        onClick={() => openModal(item)}
                                        className="btn btn-link"
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Checkout Modal */}
            {selectedItem && (
                <div className="modal" style={{ display: 'block' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{selectedItem.name}</h5>
                                <button type="button" className="btn-close" onClick={closeModal} aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                <div className="row">
                                    <div className="col-md-6">
                                        <img src={selectedItem.imageUrl} alt={selectedItem.name} />
                                        <p>Price: &#8377;{selectedItem.price}</p>
                                        <div className="item-actions">
                                            <button type="button" className='btn btn-danger' onClick={() => updateQuantity(-1)} style={{ width: '20%' }}>
                                                <i className="fa-solid fa-minus" style={{ color: "#dee0e3", display: 'contents' }}></i>
                                            </button>
                                            <span style={{ margin: '5px' }}>{selectedItem.quantity}</span>
                                            <button type="button" className='btn btn-warning' onClick={() => updateQuantity(1)} style={{ width: '20%' }}>
                                                <i className="fa-solid fa-plus" style={{ color: "#dee0e3", display: 'contents' }}></i>
                                            </button>
                                        </div>
                                    </div>
                                    <div className="col-md-6" style={{backgroundColor: 'floralwhite'}}>
                                        <div className='summary' style={{ width: '85%', marginLeft: '40px' }}>
                                            <h6 className='fs-5' style={{ color: 'black' }}>Order Summary</h6>
                                            <p style={{ color: 'black' }}>{selectedItem.name} {selectedItem.quantity}</p>
                                            <label style={{ marginBottom: '10px' }}>Shipping</label>
                                            <select className="form-select" aria-label="Default select example" onChange={handleDeliveryChange}>
                                                <option value="0">Standard Delivery</option>
                                                <option value="40">&#8377;40</option>
                                                <option value="90">&#8377;90</option>
                                                <option value="50">&#8377;50</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <p>Total: &#8377;{total}</p>
                                <button
                                    type="button"
                                    className='btn btn-success'
                                    onClick={() => {
                                        addToCart(selectedItem);
                                        closeModal(); // Close the modal
                                    }}
                                >
                                    Checkout
                                </button>
                            </div>
                        </div>
                    </div>
                </div>

            )}
        </div>
    );
};

