import React, { useState } from 'react';

const Form = ({ onClose }) => {
  const [category, setCategory] = useState('');
  const [menus, setMenus] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);

  const handleFormSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="form-overlay">
      <div className="form-container">
        <h3>Add New Item</h3>
        <form onSubmit={handleFormSubmit} style={{marginbottom: '5px'}}>
          <div className="mb-3">
            <label htmlFor="category" className="form-label">Category</label>
            <select
              id="category"
              className="form-select"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Select a category</option>
              <option value="Category 1">Chinese</option>
              <option value="Category 2">Italian</option>
              <option value="Category 3">Indian</option>
              <option value="Category 4">Beverages</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="menus" className="form-label">Menus</label>
            <input
              type="text"
              className="form-control"
              id="menus"
              value={menus}
              onChange={(e) => setMenus(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="description" className="form-label">Description</label>
            <textarea
              className="form-control"
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="file" className="form-label">Attach File</label>
            <input
              type="file"
              className="form-control"
              id="file"
              onChange={(e) => setFile(e.target.files[0])}
            />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
          <button type="button" className="btn btn-secondary" onClick={onClose}>Cancel</button>
        </form>
      </div>
    </div>
  );
};

export default Form;
