        },
        'Sienna store and Cafe': {
            items: [
                { name: 'Tandoori Roti', price: 50, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRL3Cr83zblVc1ympJqy8iW_IKzKGiiU8gXtA&usqp=CAU' },
                { name: 'Momos', price: 70, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQAEaI0igrWDKx3PRn0e5zMd1RLqQnFKkMWRw&usqp=CAU' },
                { name: 'Dal Makhni', price: 225, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQq2_Udk6tjiL7_7XnIW_T8uR_39nj3k4w3Zw&usqp=CAU' },
                { name: 'Mushroom Risotto', price: 410, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzeBbX6iODqnc8xZtswO0nwgR3eWhl9BM68g&usqp=CAU' },
                { name: 'Mix Juice', price: 135, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPajXvMbKd36wy4D56ARTfTzeuggLUxPC-fQ&usqp=CAU' },
                { name: 'Choco Bar', price: 45, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSfN8O2koAxfJQuG1YVtfRSsxy4vwLNZu-Saw&usqp=CAU' },
            ],
        },
        'Americano': {
            items: [
                { name: 'Lemonade', price: 150, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRo570tifJ9UDv-wSvweJ6HPdZ99bFTzdAI4w&usqp=CAU' },
                { name: 'Bruschetta', price: 275, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSAglMAwUAHApJ9w_2sIRM0CoEG7o-mCjGDKw&usqp=CAU' },
                { name: 'Dumplings', price: 150, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTk-lOEkAMaTqR9XVNT0l9kA8OuIajKgMslcw&usqp=CAU' },
                { name: 'Palak Paneer', price: 200, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSKEIJ5lxSpTPIZQ-uB7992kL6zaiNHqNBs8Q&usqp=CAU' },
                { name: 'Thupka', price: 99, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQSl4j9PzFDRZmojL_Y5n5Sjo8QIDRtrL0UJg&usqp=CAU' },
                { name: 'Rasmalai', price: 100, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQaGn6Dbm_3Q3ufFmvCeQOAuwI3CqIOF9taPA&usqp=CAU' },
            ],
        },
        'Palms and Sand Beach': {
            items: [
                { name: 'Veg Pulao', price: 185, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSto-ZZ131aL4EwlSVzGiZrCqt7gNaW77YeIA&usqp=CAU' },
                { name: 'Blue Mojito', price: 210, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQe7cfcF20VShHDpsUqUA0aGHbKMoGkL2EwgA&usqp=CAU' },
                { name: 'Panzenella', price: 330, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQDGg2hGu7GkD7YLRPqEDawd-f1Z9mnjM-SZg&usqp=CAU' },
                { name: 'Fry Momos', price: 80, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ8l2SeApeMzheLISDnNC2wIRbGx1okEBJEQQ&usqp=CAU' },
                { name: 'Malai Kofta', price: 245, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQjIfQ7580SqqF-WswUOGMGnuICar96ntyfmg&usqp=CAU' },
                { name: 'Gulab Jamun', price: 60, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXSUaS713bK8qNUu1yOuLKCc0K1ze6cuqDVA&usqp=CAU' },
            ],
        }
    };

    const categories = {
        Indian: {
            Breakfast: ['Butter Naan', 'Mix Veg'],
            Lunch: ['Dal Makhni', 'Malai Kofta'],
            Dinner: ['Shahi Paneer', 'Missi Roti'],
        },
        Chinese: {
            Snacks: ['Chowmein', 'Momos'],
        },
        Italian: {
            Italian: ['Pizza', 'Spaghetti', 'Bruschetta'],
        },
        Beverages: {
            Beverages: ['Juice', 'Blue Mojito'],
        },
    };

    const addToCart = (item) => {
        // Check if the item is already in the cart
        const itemIndex = cart.findIndex((cartItem) => cartItem.name === item.name);

        if (itemIndex !== -1) {
            // If it's already in the cart, update the quantity
            const updatedCart = [...cart];
            updatedCart[itemIndex].quantity += 1;
            setCart(updatedCart);
        } else {
            // If it's not in the cart, add it with quantity 1
            setCart([...cart, { ...item, quantity: 1 }]);
        }
    };

    // Function to open the modal and set the selected item
    const openModal = (item) => {
        setSelectedItem({ ...item, quantity: 0 });
    };

    // Function to close the modal
    const closeModal = () => {
        setSelectedItem(null);
        props.showAlert("Checkout Successfully", "success");
    };

    // Function to update quantity in the modal
    const updateQuantity = (value) => {
        setSelectedItem({ ...selectedItem, quantity: selectedItem.quantity + value });
    };

    return (
        <div className="container">
            <h2 className="pb-3 fs-4">{restaurantName}</h2>
            {Object.keys(categories).map((category, index) => (
                <div key={index}>
                    <h3>{category}</h3>
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th>Item</th>
                                <th>Image</th>
                                <th>Price</th>
                                <th>Add to Cart</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Object.keys(categories[category]).map((subCategory) =>
                                restaurants[restaurantName].items
                                    .filter((item) => categories[category][subCategory].includes(item.name))
                                    .map((item, itemIndex) => (
                                        <tr key={itemIndex}>
                                            <td>{item.name}</td>
                                            <td>
                                                <img src={item.imageUrl} alt={item.name} style={{ width: '50px' }} />
                                            </td>
                                            <td>&#8377;{item.price}</td>
                                            <td>
                                                <button
                                                    type="button"
                                                    onClick={() => openModal(item)}
                                                    className="btn btn-link"
                                                >
                                                    Add to Cart
                                                </button>
                                            </td>
                                        </tr>
                                    ))
                            )}
                        </tbody>
                    </table>
                </div>
            ))}

            {/* Checkout Modal */}
            {selectedItem && (
                <div className="modal" style={{ display: 'block' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{selectedItem.name}</h5>
                                <button type="button" className="btn-close" onClick={closeModal} aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                <img src={selectedItem.imageUrl} alt={selectedItem.name} />
                                <p>Price: &#8377;{selectedItem.price}</p>
                                <div className="item-actions">
                                    <button type="button" className="btn btn-danger" onClick={() => updateQuantity(-1)} style={{ width: '10%' }}>
                                        <i className="fa-solid fa-minus" style={{ color: "#dee0e3", display: 'contents' }}></i>
                                    </button>
                                    <span style={{ margin: '5px' }}>{selectedItem.quantity}</span>
                                    <button type="button" className="btn btn-warning" onClick={() => updateQuantity(1)} style={{ width: '10%' }}>
                                        <i className="fa-solid fa-plus" style={{ color: "#dee0e3", display: 'contents' }}></i>
                                    </button>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <p>Total: &#8377;{selectedItem.price * selectedItem.quantity}</p>
                                <button
                                    type="button"
                                    className="btn btn-success"
                                    onClick={() => {
                                        addToCart(selectedItem);
                                        closeModal(); // Close the modal
                                    }}
                                >
                                    Checkout
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};
export default OrderNow;
