import React, { useState, useContext, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import menuContext from '../context/menuContext';

const Category = () => {
  const navigate = useNavigate();
  const context = useContext(menuContext);
  const { menuData, deleteMenu, editMenu, getMenu } = context;
  const categories = ["Chinese", "Italian", "Indian", "Beverages"];

  const ref = useRef(null);
  const refClose = useRef(null);

  const [selectedCategory, setSelectedCategory] = useState("");
  const [menu, setMenu] = useState({
    id: "",
    ename: "",
    ecategory: "",
    eprice: "",
    edescription: "",
    efile: "",
  }); // State to store fetched data

  useEffect(() => {
    if(localStorage.getItem('token')){
      getMenu()
    }
    else{
       navigate("/login");
    }
      // eslint-disable-next-line
  }, []);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  const handleLogout = () => {
    // Redirect to the login page
    navigate("/login");
  };
  
  const updateMenu = (currentMenu) => {
    ref.current.click();
    setNote({id: currentMenu._id, ename: currentMenu.name, ecategory: currentMenu.category, eprice: currentMenu.price,
      edescription: currentMenu.description, efile: currentMenu.file })
 }
 
 const handleClick = (e) =>{
  editMenu(menu.id, menu.ename, menu.ecategory, menu.eprice, menu.edescription, menu.efile)
  refClose.current.click();
  // props.showAlert("Updated Successfully", "success");
}

  const onChange = (e) => {
    setMenu({ ...menu, [e.target.name]: e.target.value });
  };

return (
