import React, { useReducer } from 'react';
import MenuDataContext from './menuContext'; // Import the new context

function menuDataReducer(state, action) {
    switch (action.type) {
        case 'SET_MENU_DATA':
            return action.payload;
        case 'ADD_MENU':
            return [...state, action.payload];
        // Add more cases for edit and delete actions if needed
        default:
            return state;
    }
}

const MenuState = (props) => {
    const host = "http://localhost:5000";

    const [menuData, setMenuData, dispatch] = useReducer(menuDataReducer, []);

    const getMenu = async () => {

        // API CALL
        const response = await fetch(`${host}/api/menuData/fetchmenudata`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },

        });
        const json = await response.json()
        setMenuData(json);
    }

    // Add a menu      
    const addMenu = async (name, category, price, description, file) => {
        try {
            // Make a POST request to add the new menu item
            const response = await fetch(`${host}/api/menuData/addmenu`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "auth-token": localStorage.getItem('token')
                },
                body: JSON.stringify({ name, category, price, description, file })
            });

            if (!response.ok) {
                // Handle the error if the POST request fails
                throw new Error('Failed to add a new menu item.');
            }

            // Parse the response to get the newly added menu item
            const menu = await response.json();

            // Dispatch the 'ADD_MENU' action with the new menu item as payload
            dispatch({ type: 'ADD_MENU', payload: menu });

            // Optionally, you can show a success message or perform other actions
            console.log('Menu item added successfully.');
        } catch (error) {
            // Handle any errors that occurred during the POST request
            console.error(error.message);
        }
    }

    // Delete a note
    const deletemenu = async (id) => {
        // API Call
        const response = await fetch(`${host}/api/menuData/deletemenu/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
                "auth-token": localStorage.getItem('token')
            },
        });

        if (!response.ok) {
            // Handle the error if the delete request fails
            throw new Error(`Failed to delete menu item with id ${id}`);
        }

        // Dispatch the 'DELETE_MENU' action with the id
        dispatch({ type: 'DELETE_MENU', payload: id });
        const json = response.json(); // parses JSON response into native JavaScript objects
        console.log(json)

        console.log("Deleting the menu with id" + id)
        const newMenuData = menuData.filter((menu) => menu._id !== id); // Assuming _id is the unique identifier
        setMenuData(newMenuData);
    }

    // Edit a note
    const editMenu = async (id, name, category, price, description, file) => {
        // API Call
        const response = await fetch(`${host}/api/menuData/updatemenu/${id}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "auth-token": localStorage.getItem('token')
            },

            body: JSON.stringify({ name, category, price, description, file }) // body data type must match "Content-Type" header
        });
        const json = await response.json(); // parses JSON response into native JavaScript objects
        console.log(json);
        let newMenuData = JSON.parse(JSON.stringify(menuData))
        // Logic to edit in client
        for (let index = 0; index < newMenuData.length; index++) {
            const element = newMenuData[index];
            if (element._id === id) {
                newMenuData[index].name = name;
                newMenuData[index].category = category;
                newMenuData[index].price = price;
                newMenuData[index].description = description;
                newMenuData[index].file = file;
                break;
            }
        }
        setMenuData(newMenuData);
    }

    return (
        <MenuDataContext.Provider value={{ menuData, addMenu, getMenu, editMenu, deletemenu }}>
            {props.children}
        </MenuDataContext.Provider>
    )
}

export default MenuState;
