import React from 'react';
import { useLocation } from 'react-router-dom';

const BuyNow = () => {
    const location = useLocation();
    
    if (!location.state || !location.state.orderSummary) {
        return (
            <div>
                <h2>Order Summary</h2>
                <p>No order summary data available.</p>
            </div>
        );
    }
    // Access the order summary from the location state
    const orderSummary = location.state.orderSummary;

    // Calculate total price and total quantity from orderSummary
    const totalPrice = orderSummary.reduce(
        (total, item) => total + item.price * item.quantity,
        0
    );
    const totalQuantity = orderSummary.reduce(
        (total, item) => total + item.quantity,
        0
    );

    return (
        <div>
            <h2>Order Summary</h2>
            <ul>
                {orderSummary.map((item, index) => (
                    <li key={index}>
                        {item.name} - Quantity: {item.quantity}, Price: {item.price}
                    </li>
                ))}
            </ul>
            <h3>Total Price: {totalPrice}</h3>
            <h3>Total Quantity: {totalQuantity}</h3>
        </div>
    );
};

export default BuyNow;
