import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';

const OrderNow = () => {
    const [cart, setCart] = useState([]); // Initialize an empty cart
    const { restaurantName } = useParams();

    const categories = [
        {
            name: 'Indian',
            items: [
                { name: 'Butter Naan', price: 50, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRGk-LKmmf8Do_eFTsaqOrU3VFD2X87DgDjvw&usqp=CAU' },
                { name: 'Shahi Paneer', price: 220, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQKd9N93vE5cHbVgusvrwonDm1Yzgk8GP1y6A&usqp=CAU' },
                { name: 'Mix Veg', price: 110, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUD95rTk5BhESm4y557G2JncSpHhljGADPzQ&usqp=CAU' },
            ],
        },
        {
            name: 'Chinese',
            items: [
                { name: 'Item 1', description: 'Description 1', price: 15, image: 'chinese_item1.jpg' },
                { name: 'Item 2', description: 'Description 2', price: 18, image: 'chinese_item2.jpg' },
                // Add more Chinese items
            ],
        },
        {
            name: 'Italian',
            items: [
                { name: 'Item 1', description: 'Description 1', price: 20, image: 'italian_item1.jpg' },
                { name: 'Item 2', description: 'Description 2', price: 22, image: 'italian_item2.jpg' },
                // Add more Italian items
            ],
        },
        {
            name: 'Beverages',
            items: [
                { name: 'Item 1', description: 'Description 1', price: 5, image: 'beverage_item1.jpg' },
                { name: 'Item 2', description: 'Description 2', price: 4, image: 'beverage_item2.jpg' },
                // Add more Beverage items
            ],
        },
    ];

    // Function to add an item to the cart
    const addToCart = (item) => {
        setCart([...cart, item]);
    };

    const selectedCategory = categories.find(category => category.name === restaurantName);

    return (
        <div className="container">
            <h3>{restaurantName}</h3>
            <div className="row row-cols-1 row-cols-md-3 g-4">
                {selectedCategory.items.map((item, index) => (
                    <div key={index} className="col">
                        <div className="card">
                            <img src={item.image} alt={item.name} className="card-img-top" />
                            <div className="card-body">
                                <h5 className="card-title">{item.name}</h5>
                                <p className="card-text">&#8377;{item.price}</p>
                                <div className="card-button" style={{ position: 'absolute', bottom: '25px', right: '10px' }}>
                                    <button type="button"
                                        onClick={() => addToCart(item)}
                                        className="btn btn-link"
                                    >
                                        <Link to="/cart"> Add to Cart </Link>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default OrderNow;
