// import { createContext } from "react";

// const menuContext = createContext();

// export default menuContext;

import { createContext, useContext, useReducer } from 'react';

// Define your action types
const ADD_MENU = 'ADD_MENU';

// Define your initial state and reducer function
const initialState = {
  menus: [], // You may have other properties in your state
};

const menuReducer = (state, action) => {
  switch (action.type) {
    case ADD_MENU:
      return {
        ...state,
        menus: [...state.menus, action.payload],
      };
    // Handle other action types if needed
    default:
      return state;
  }
};

// Create your context
const MenuContext = createContext();

// Create a custom hook to access the context and dispatch function
export const useMenuContext = () => {
  return useContext(MenuContext);
};

// Create a context provider component
export const MenuProvider = ({ children }) => {
  const [state, dispatch] = useReducer(menuReducer, initialState);

  // Define your action creators
  const addMenu = (name, category, price, description, file) => {
    const newMenu = { name, category, price, description, file };
    dispatch({ type: ADD_MENU, payload: newMenu });
  };

  // You can add more action creators here if needed

  return (
    <MenuContext.Provider value={{ state, addMenu }}>
      {children}
    </MenuContext.Provider>
  );
};
