import React, { useState } from 'react'
import "./styles.css";

const Category = () => {
  const CategoryItem = ({ categoryName }) => {
    const [showModal, setShowModal] = useState(false);
  
    const handleClose = () => {
      setShowModal(false);
    };
  
    const handleShow = () => {
      setShowModal(true);
    };
  
    return (
      <div>
        <span>{categoryName}</span>
        <Button variant="primary" onClick={handleShow}>
          Edit/Delete
        </Button>
  
        <Modal show={showModal} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Edit/Delete Category</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {/* Your edit and delete options go here */}
            <p>Edit Category</p>
            <p>Delete Category</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  };
}

export default Category
