// import { createContext } from "react";

// const menuContext = createContext();

// export default menuContext;

import { createContext, useContext, useReducer, useState } from 'react';

// Define your action types
const ADD_MENU = 'ADD_MENU';
const host = "http://localhost:5000";

// Define your initial state and reducer function
const initialState = {
  menus: [], // You may have other properties in your state
};

const menuReducer = (state, action) => {
  switch (action.type) {
    case ADD_MENU:
      return {
        ...state,
        menus: [...state.menus, action.payload],
      };
    // Handle other action types if needed
    default:
      return state;
  }
};

// Create your context
const MenuContext = createContext();

// Create a custom hook to access the context and dispatch function
export const useMenuContext = () => {
  return useContext(MenuContext);
};

// Create a context provider component
export const MenuProvider = ({ children }) => {
  const [, dispatch] = useReducer(menuReducer, initialState);
  const [menuData, setMenuData] = useState([]); // Use a different name here

  // Define your action creators
  const addMenu = (name, category, price, description, file) => {
    const newMenu = { name, category, price, description, file };
    dispatch({ type: ADD_MENU, payload: newMenu });
  };

  const getMenu = async () => {
    try {
      const response = await fetch(`${host}/api/menuData/fetchmenudata`);
      const data = await response.json();
      setMenuData(data); // Set the menuDataState with the fetched data
    } catch (error) {
      console.error("Error fetching menu data:", error);
    }
  };
  
  const deletemenu = async (menuId) => {
    try {
      // Implement the logic to delete the menu item here
      const response = await fetch(`${host}/api/menuData/deletemenu/${menuId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token'),
        },
      });

      if (response.ok) {
        console.log(`Successfully deleted menu with id: ${menuId}`);
        // Remove the deleted menu item from the state
        const newMenuData = menuData.filter((menu) => menu._id !== menuId);
        setMenuData(newMenuData);
      } else {
        console.error(`Error deleting menu item with id: ${menuId}`);
      }
    } catch (error) {
      console.error('Error deleting menu item:', error);
    }
  };

  return (
    <MenuContext.Provider value={{ state: menuData, addMenu, getMenu, deletemenu }}>
      {children}
    </MenuContext.Provider>
  );
};
