import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';

const OrderNow = () => {
    const { restaurantName } = useParams();
    const restaurantData = restaurants[restaurantName];

    // Initialize an empty cart with quantity
    const [cart, setCart] = useState(restaurantData.items.map(item => ({ ...item, quantity: 0 }))); 

    // Function to add an item to the cart
    const addToCart = (item) => {
        const updatedCart = cart.map(cartItem => {
            if (cartItem.name === item.name) {
                return { ...cartItem, quantity: cartItem.quantity + 1 };
            }
            return cartItem;
        });
        setCart(updatedCart);
    };

    // Function to remove an item from the cart
    const removeFromCart = (item) => {
        const updatedCart = cart.map(cartItem => {
            if (cartItem.name === item.name && cartItem.quantity > 0) {
                return { ...cartItem, quantity: cartItem.quantity - 1 };
            }
            return cartItem;
        });
        setCart(updatedCart);
    };

    return (
        <div className="container">
            <h2 className="pb-3 fs-4">{restaurantName}</h2>
            <div className="row row-cols-1 row-cols-md-3 g-4" style={{ marginBottom: '20px' }}>
                {restaurantData.items.map((item, index) => (
                    <div key={index} className="col">
                        <div className="card">
                            <img src={item.imageUrl} alt={item.name} className="card-img-top" style={{ height: '200px' }} />
                            <div className="card-body">
                                <h5 className="card-title">{item.name}</h5>
                                <p className="card-text">&#8377;{item.price}</p>
                                <div className="card-button" style={{ position: 'absolute', bottom: '25px', right: '10px' }}>
                                    <button type="button" onClick={() => addToCart(item)} className="btn btn-link">
                                        Add to Cart
                                    </button>
                                    <div className="quantity-controls">
                                        <button onClick={() => removeFromCart(item)}>-</button>
                                        <span>{item.quantity}</span>
                                        <button onClick={() => addToCart(item)}>+</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <Link to="/cart">Checkout</Link>
        </div>
    );
};

export default OrderNow;
