import React, {useState} from 'react'
import "./styles.css";
import { useNavigate } from "react-router-dom";

const Signup = () => {
    const navigate = useNavigate();
    const [name, setname] = useState("");
    const [email, setemail] = useState("");
    const [password, setpassword] = useState("");
    const [cpassword, setcpassword] = useState("");    
    const [formEnabled, setFormEnabled] = useState(false);
    const users = [
        { name: '', email: '', password: '', cpassword: '' }
    ];

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:3000/api/auth/createuser", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ name, email, password, cpassword })
        });
        const json = await response.json()
        console.log(json);
        const account = users.find((user) => user.name === name);
        if (account && account.password === password) {
            localStorage.setItem("authenticated", true);
            navigate("/");
        }
    };
    return (
        <div className='container mt-3'>
            <h2>Signup to use Food Ordering</h2>

            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input type="text" className="form-control" id="name" name="name" aria-describedby="emailHelp"
                        onChange={(e) => {
                            setname(e.target.value);
                            setFormEnabled(true);
                        }}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email address</label>
                    <input type="email" className="form-control" id="email" name="email" aria-describedby="emailHelp"
                        onChange={(e) => {
                            setemail(e.target.value);
                            setFormEnabled(true);
                        }}
                    />
                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input type="password" className="form-control" id="password" name="password"
                        onChange={(e) => {
                            setpassword(e.target.value);
                            setFormEnabled(true);
                        }}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="cpassword" className="form-label">Confirm Password</label>
                    <input type="password" className="form-control" id="cpassword" name="cpassword"
                        onChange={(e) => {
                            setcpassword(e.target.value);
                            setFormEnabled(true);
                        }}
                    />
                </div>
                <button type="submit" className="btn btn-primary" disabled={!formEnabled}>Signup</button>
            </form>
        </div>
    )
}

export default Signup
