import React, { useState } from 'react';

const BuyNow = ({ selectedItems, onBuy }) => {
    const [showOrderHistory, setShowOrderHistory] = useState(false);

    const handleBuyClick = () => {
        onBuy();
        setShowOrderHistory(true);
    };

    const handleCloseOrderHistory = () => {
        setShowOrderHistory(false);
        // window.location.reload(); // This line refreshes the page - you might want to reconsider this, as it refreshes the whole page
    };

    return (
        <div>
            {showOrderHistory ? (
                <div>
                    <h2>Order History</h2>
                    <ul style={{ listStyle: 'none' }}>
                        {selectedItems.map((item, index) => {
                            // Calculate the total for the current item
                            const currentItemTotal = item.price * item.quantity;

                            return (
                                <li key={index}>
                                    <div>
                                        <img src={item.imageUrl} alt={item.name} />
                                    </div>
                                    <div>
                                        <p>Name: {item.name}</p>
                                        <p>Price: &#8377;{item.price}</p>
                                        <p>Quantity: {item.quantity}</p>
                                        <p>Total: &#8377;{currentItemTotal}</p>
                                    </div>
                                </li>
                            );
                        })}
                    </ul>
                    <button onClick={handleCloseOrderHistory}>Close Order History</button>
                </div>
            ) : (
                <div>
                    <h2>Selected Items</h2>
                    <ul style={{ listStyle: 'none' }}>
                        {selectedItems.map((item, index) => {
                            return (
                                <li key={index}>
                                    <div>
                                        <img src={item.imageUrl} alt={item.name} />
                                    </div>
                                    <div>
                                        <p>Name: {item.name}</p>
                                        <p>Price: &#8377;{item.price}</p>
                                        <p>Quantity: {item.quantity}</p>
                                    </div>
                                </li>
                            );
                        })}
                    </ul>
                    <button onClick={handleBuyClick}>Buy Now</button>
                </div>
            )}
        </div>
    );
};

export default BuyNow;
