import './App.css';
import About from './components/About';
import Admin from './components/Admin';
import Category from './components/Category';
import Dashboard from './components/Dashboard';
import Home from './components/Home';
import Login from './components/Login';
import Navbar from './components/Navbar';
import Signup from './components/Signup';
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import MenuState from './context/MenuState';

function App() {
  // Assume you have a way to determine the user's role (e.g., from authentication)
  const userRole = 'admin'; // Replace with your actual user role logic

  return (
    <MenuState>
      <Router>
        <Navbar />
        <div className='container'>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/category" element={userRole === 'merchant' ? <Category /> : <Navigate to="/" />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/admin" element={userRole === 'admin' ? <Admin /> : <Navigate to="/" />} />
          </Routes>
        </div>
      </Router>
    </MenuState>
  );
}

export default App;
