import React, {useState} from 'react';

const BuyNow = ({ selectedItems, onBuy, props, cart }) => {

    const [showBuyNow] = useState(false); 
    const handleBuy = () => {
        props.showAlert('Order Placed Successfully', 'success');
    };

    return (
        <div>
            <h2>Selected Items</h2>
            <ul>
                {selectedItems.map((item, index) => (
                    <li key={index}>
                        <div>
                            <img src={item.imageUrl} alt={item.name} />
                        </div>
                        <div>
                            <p>Name: {item.name}</p>
                            <p>Price: &#8377;{item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                            <p>Total: &#8377;{item.price * item.quantity}</p>
                        </div>
                    </li>
                ))}
            </ul>
            <button onClick={onBuy} className="btn btn-success">
                Buy
            </button>
            {showBuyNow && (
                <BuyNow selectedItems={cart} onBuy={handleBuy} />
            )}
        </div>
    );
};

export default BuyNow;
