import React, { useState } from 'react'
import "./styles.css";
import 'font-awesome/css/font-awesome.min.css';

const Category = () => {

  const [isOpen, setIsOpen] = useState(false);

  const toggleCard = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className='container mt-3' style={{ display: 'flex', justifyContent: 'space-between' }}>
      <button type="button" className="btn btn-light" onClick={toggleCard}> 
         {isOpen ? "Close Card" : "Open Card"}
        Chinese <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
      <div className="card" style={{width: "18rem"}}>
        <div className="card-body">
          <h5 className="card-title">Card title</h5>
          <i className="fa-regular fa-pen-to-square"></i>
          <i className="fa-solid fa-trash"></i>
        </div>
      </div>

      <button type="button" className="btn btn-light">
        Italian <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
      <button type="button" className="btn btn-light">
        Indian <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
      <button type="button" className="btn btn-light">
        Beverages <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
    </div >
  );
};

export default Category
