import React, { useContext, useState } from 'react'
import menuContext from '../context/menuContext';

const CategoryItem = () => {
    const context = useContext(menuContext);
    const { deletemenu } = context;
    const { menu, updateMenu, setMenu } = useState();

    const onChange = (e) => {
        setMenu({ ...menu, [e.target.name]: e.target.value })
    }

    return (
        <div className='col-md-3'>
            <div className="card my-3">
                <div className="card-body">
                    <h5 className="card-title">{menu.category}</h5>
                    <div className="mb-3">
                        <label htmlFor="price" className="form-label">Price</label>
                        <input type="number" className="form-control" id="price" name="price"
                            value={menu.price} onChange={onChange} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="file" className="form-label">Select a File</label>
                        <input className="form-control" type="text" id="file" name="file"
                            value={menu.file} onChange={onChange} />
                    </div>
                    <i className="fa-solid fa-trash mx-2" onClick={() => { deletemenu(menu._id); }}></i>
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={()=>{updateMenu(menu)}}></i>
                </div>
            </div>
        </div>
    )
}

export default CategoryItem
