import React from 'react';

const BuyNow = ({ cart }) => {
    return (
        <div>
            <h2>Buy Now</h2>
            <table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map((item) => (
                        <tr key={item.name}>
                            <td>{item.name}</td>
                            <td>&#8377;{item.price}</td>
                            <td>{item.quantity}</td>
                            <td>&#8377;{item.price * item.quantity}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button type="button" className="btn btn-primary">
                Purchase
            </button>
        </div>
    );
};

export default BuyNow;
