import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";

const Admin = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        // Redirect to the login page
        navigate('/login');
    };

    // Example restaurant data
    const [restaurant, setRestaurantData] = useState([]);

    const restaurants = async (id, name, location) =>{
        const response = await fetch('/api/auth/restaurants', {
            method: "POST", 
            headers: {
              "Content-Type": "application/json",
              "auth-token": localStorage.getItem('token')
            },
           
            body: JSON.stringify({id, name, location}) // body data type must match "Content-Type" header
          });
            
            const restaurant = await response.json();
            setRestaurantData(restaurant.concat(restaurant))
      }
    

    return (
        <>
            <h3>Welcome to the Admin Page</h3>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Name</th>
                        <th scope="col">Location</th>
                    </tr>
                </thead>
                <tbody>
                    {restaurant.map((restaurant) => (
                        <tr key={restaurant.id}>
                            <td>{restaurant.id}</td>
                            <td>{restaurant.name}</td>
                            <td>{restaurant.location}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button type="button" className="btn btn-primary" onClick={handleLogout}>Logout</button>
        </>
    );
};

export default Admin;
