import React, { useState } from 'react';

const BuyNow = ({ cart, selectedItem, continueShopping }) => {
  const [selectedCard, setSelectedCard] = useState(''); // To store the selected card
  const [cardNumber, setCardNumber] = useState('');
  const [cardHolder, setCardHolder] = useState('');
  const [expires, setExpires] = useState('');
  const [cvv, setCVV] = useState('');

  // Function to handle card selection
  const handleCardSelection = (card) => {
    setSelectedCard(card);
  };

  // Function to handle the BuyNow button click
  const handleBuyNow = () => {
    // Perform payment processing here, you can add your logic
    // Once payment is successful, you can show a success message and allow continuing shopping

    // Clear payment form fields and selected card
    setSelectedCard('');
    setCardNumber('');
    setCardHolder('');
    setExpires('');
    setCVV('');
  };

  return (
    <div className="container">
      <h2>Order Summary</h2>
      {selectedItem && (
        <div>
          <img src={selectedItem.imageUrl} alt={selectedItem.name} />
          <p>Name: {selectedItem.name}</p>
          <p>Price: â‚¹{selectedItem.price}</p>
          <p>Quantity: {selectedItem.quantity}</p> {/* Quantity might be hardcoded to 1, depending on your logic */}
        </div>
      )}

      <h2>Payment Options</h2>
      <div className="dropdown">
        <button
          className="btn btn-secondary dropdown-toggle"
          type="button"
          id="cardDropdown"
          data-bs-toggle="dropdown"
          aria-expanded="false"
        >
          {selectedCard || 'Select Card'}
        </button>
        <ul className="dropdown-menu" aria-labelledby="cardDropdown">
          <li onClick={() => handleCardSelection('Card 1')}>Card 1</li>
          <li onClick={() => handleCardSelection('Card 2')}>Card 2</li>
          {/* Add more card options as needed */}
        </ul>
      </div>

      <input
        type="text"
        placeholder="Card Number"
        value={cardNumber}
        onChange={(e) => setCardNumber(e.target.value)}
      />
      <input
        type="text"
        placeholder="Card Holder"
        value={cardHolder}
        onChange={(e) => setCardHolder(e.target.value)}
      />
      <input
        type="text"
        placeholder="Expires"
        value={expires}
        onChange={(e) => setExpires(e.target.value)}
      />
      <input
        type="text"
        placeholder="CVV"
        value={cvv}
        onChange={(e) => setCVV(e.target.value)}
      />

      <button onClick={handleBuyNow} className="btn btn-primary">
        Buy Now
      </button>
      <button onClick={continueShopping} className="btn btn-secondary">
        Continue Ordering
      </button>
    </div>
  );
};

export default BuyNow;
