import React from 'react';

const BuyNow = (props) => {
    const { selectedItems } = props.location.state;

    return (
        <div>
            <h2>Buy Now</h2>
            {selectedItems.map((item, index) => (
                <div key={index}>
                    <div>
                        <img src={item.imageUrl} alt={item.name} />
                    </div>
                    <div>
                        <p>Name: {item.name}</p>
                        <p>Price: &#8377;{item.price}</p>
                        <p>Quantity: {item.quantity}</p>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default BuyNow;
