import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const BuyNow = ({ selectedItems }) => {
    const [cardType, setCardType] = useState('Visa'); // Initialize with a default card type
    const [cardNumber, setCardNumber] = useState('');
    const [cardHolder, setCardHolder] = useState('');
    const [expires, setExpires] = useState('');
    const [cvv, setCVV] = useState('');
    const navigate = useNavigate();

    const handleBuyNow = () => {
        props.showAlert("Order Placed Successfully", "success");
        navigate("/OrderNow");
    };

    return (
        <div>
            <h2>Order Summary</h2>
            <ul>
                {selectedItems.map((item) => (
                    <li key={item.id}>{item.name} - ${item.price}</li>
                ))}
            </ul>
            <p>Total: ${selectedItems.reduce((total, item) => total + item.price, 0)}</p>

            <div className="card-payment-options">
                <div className="dropdown">
                    <button
                        className="btn btn-secondary dropdown-toggle"
                        type="button"
                        id="cardTypeDropdown"
                        data-toggle="dropdown"
                        aria-haspopup="true"
                        aria-expanded="false"
                    >
                        {cardType}
                    </button>
                    <div className="dropdown-menu" aria-labelledby="cardTypeDropdown">
                        <button className="dropdown-item" onClick={() => setCardType('Visa')}>
                            Visa
                        </button>
                        <button className="dropdown-item" onClick={() => setCardType('MasterCard')}>
                            MasterCard
                        </button>
                        {/* Add more card types as needed */}
                        <button className="dropdown-item" onClick={() => setCardType('MasterCard')}>
                            Maestro
                        </button>
                    </div>
                </div>
                <input
                    type="text"
                    className="form-control"
                    placeholder="Card Number"
                    value={cardNumber}
                    onChange={(e) => setCardNumber(e.target.value)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Card Holder"
                    value={cardHolder}
                    onChange={(e) => setCardHolder(e.target.value)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Expires"
                    value={expires}
                    onChange={(e) => setExpires(e.target.value)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="CVV"
                    value={cvv}
                    onChange={(e) => setCVV(e.target.value)}
                />
                <button className="btn btn-primary" onClick={handleBuyNow}>
                    Buy Now
                </button>
            </div>
        </div>
    );
};

export default BuyNow;
