import React from 'react'
import "./styles.css"
import Form from './Form'

const Menu = () => {
    const [isFormOpen, setIsFormOpen] = useState(false);

    const handleAddNewClick = () => {
        setIsFormOpen(true);
    };

    const handleCloseForm = () => {
        setIsFormOpen(false);
    };

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Card 1</h5>
                            <p className="card-text">This is the content of card 1.</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Card 2</h5>
                            <p className="card-text">This is the content of card 2.</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Card 3</h5>
                            <p className="card-text">This is the content of card 3.</p>
                        </div>
                    </div>
                </div>
                <button className="btn btn-info mt-3" onClick={handleAddNewClick}>Add New</button>
                  {isFormOpen && <Form onClose={handleCloseForm} />}
            </div>
        </div>
    )
}


export default Menu
