import React, { useContext, useRef } from 'react'
import menuContext from '../context/menuContext';

const CategoryItem = (props) => {
    const context = useContext(menuContext);
    const { deletemenu } = context;
    const { menu, updateMenu } = props;
    const ref = useRef(null);

    return (
        <div className='col-md-3'>
            <div className="card my-3">
                <div className="card-body">
                    <h5 className="card-title">{menu.category}</h5>
                    <img src={menu.file} className="card-img-top" alt="..." />
                    <button ref={ref} type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                    <i className="fa-solid fa-trash mx-2" onClick={() => { deletemenu(menu._id); }}></i>
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={()=>{updateMenu(menu)}}></i></button>
                </div>
            </div>
        </div>
    )
}

export default CategoryItem
