import React, { useContext } from 'react'
import menuContext from '../context/menuData/menuContext';

const CategoryItem = (props) => {
    const context = useContext(menuContext);
    const { deletemenu } = context;
    const { menu, updateMenu } = props;
    
    const [showMore, setShowMore] = useState(false);

    const toggleText = () => {
        setShowMore(!showMore);
    };
    return (
        <div className='col-md-3'>
            <div className="card my-3">
                <div className="card-body">
                    <img src={menu.file} className="card-img-top" alt="..." />
                    <h5 className="card-title mt-1">{menu.category}</h5>
                    <p className='card-title'>Price: {menu.price}</p>
                    <p className='card-title'>Description:{menu.description.length > 50 ? (
                        <div>
                            {showMore ? menu.description : menu.description.slice(0, 50) + '... '}
                            <a className="read-more-link" onClick={toggleText}>
                                {showMore ? 'Read less' : 'Read more'}
                            </a>
                        </div>
                    ) : null}</p>
                    <i className="fa-solid fa-trash mx-2 mt-2" onClick={() => {
                        deletemenu(menu._id);
                        props.showAlert("Deleted Successfully", "success")
                    }}></i>
                    <button type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                        <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menu) }}></i></button>
                </div>
            </div>
        </div>
    )
}

export default CategoryItem
