import React, { useState } from 'react'
import "./styles.css";
import 'font-awesome/css/font-awesome.min.css';

const Category = () => {

  const [isModalOpen, setIsModalOpen] = useState(false);
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className='container mt-3' style={{ display: 'flex', justifyContent: 'space-between' }}>
      <button type="button" className="btn btn-light" data-bs-toggle="modal" data-bs-target="#exampleModal" onClick={toggleModal}
          style={{ cursor: 'pointer' }}>
        Chinese <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
                <h6 className="modal-title fs-5" id="exampleModalLabel">Category 1</h6>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
                style={{position: 'absolute', right: '5px'}}></button>
              <div className="modal-body">
               <i className="fa-regular fa-pen-to-square mx-2"></i>
               <i className="fa-solid fa-trash mx-2"></i>              
              </div>
            </div>
          </div>
        </div>
      <button type="button" className="btn btn-light">
        Italian <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
      <button type="button" className="btn btn-light">
        Indian <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
      <button type="button" className="btn btn-light">
        Beverages <i className="fa-solid fa-ellipsis-vertical mx-2"></i>
      </button>
    </div>
  );
};

export default Category
