import React, { useState } from 'react'
import "./styles.css";

const Category = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [showActions, setShowActions] = useState(false);

  const categories = ['Chinese', 'Italian', 'Indian', 'Beverages'];

  const handleCategoryChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedCategory(selectedValue);
    setShowActions(true);
  };

  const handleEdit = () => {
    // Handle edit action for the selected category
    console.log(`Editing ${selectedCategory}`);
  };

  const handleDelete = () => {
    // Handle delete action for the selected category
    console.log(`Deleting ${selectedCategory}`);
    setSelectedCategory('');
    setShowActions(false);
  };

  return (
    <div className='container mt-3'>
      <div className="card">
        <div className="card-body">
          <label htmlFor="category">Category Name: </label>
          <select className="select" id="category" onChange={handleCategoryChange} value={selectedCategory}>
            <option value="">Select</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>

          {showActions && (
            <div className='mt-3'>
              <button className="btn btn-primary" onClick={handleEdit}>Edit</button>
              <button className="btn btn-primary" onClick={handleDelete} style={{marginLeft: '5px'}}>Delete</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Category
