import React, { useState } from 'react'
// import { Link } from 'react-router-dom'

const Category = () => {
    // return (
    //     <div className='container mt-3'>
    //         <div className="dropdown">
    //             <Link className="btn btn-secondary dropdown-toggle" to="/" role="button" data-bs-toggle="dropdown" aria-expanded="false">
    //                 Food Categories
    //             </Link>

    //             <ul className="dropdown-menu">
    //                 <li><Link className="dropdown-item" to="/chinese">Chinese</Link></li>
    //                 <li><Link className="dropdown-item" to="/italian">Italian</Link></li>
    //                 <li><Link className="dropdown-item" to="/indian">Indian</Link></li>
    //                 <li><Link className="dropdown-item" to="/beverages">Beverages</Link></li>
    //             </ul>
    //         </div>
    //     </div>
    // )
    const [selectedCategory, setSelectedCategory] = useState('');
    const [showActions, setShowActions] = useState(false);
  
    const categories = ['Category 1', 'Category 2', 'Category 3', 'Category 4'];
  
    const handleCategoryChange = (event) => {
      const selectedValue = event.target.value;
      setSelectedCategory(selectedValue);
      setShowActions(true);
    };
  
    const handleEdit = () => {
      // Handle edit action for the selected category
      console.log(`Editing ${selectedCategory}`);
    };
  
    const handleDelete = () => {
      // Handle delete action for the selected category
      console.log(`Deleting ${selectedCategory}`);
      setSelectedCategory('');
      setShowActions(false);
    };
  
    return (
      <div>
        <label htmlFor="category">Select a category: </label>
        <select id="category" onChange={handleCategoryChange} value={selectedCategory}>
          <option value="">Select</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        
        {showActions && (
          <div>
            <button onClick={handleEdit}>Edit</button>
            <button onClick={handleDelete}>Delete</button>
          </div>
        )}
      </div>
    );
  };

export default Category
