import React, { useState, useContext } from 'react'
import menuContext from '../context/menuContext';

const AddMenu = ({ showAlert, updateMenu }) => {
    const context = useContext(menuContext);
    const { addMenu } = context;
    const host = "http://localhost:5000";

    const [menu, setMenu] = useState({ name: "", category: "", price: "", description: "", file: "" })
    const [selectedCategory, setSelectedCategory] = useState("");
    const categories = ["Chinese", "Italian", "Indian", "Beverages"];

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${host}/api/menuData/addmenu`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'auth-token': localStorage.getItem('token'),
                },
                body: JSON.stringify(menu),
            });

            if (response.ok) {
                const newMenu = await response.json();
                showAlert('Menu item added successfully', 'success');

                // Call the updateMenuData function to update the Category page state
                updateMenu(newMenu);

                // Clear the form
                setMenu({
                    name: '',
                    category: '',
                    price: '',
                    description: '',
                    file: '',
                });
            } else {
                showAlert('Error adding menu item', 'error');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };
    // addMenu(menu.name, menu.category, menu.price, menu.description, menu.file);
    // // setMenu({ name: "", category: "", price: "", description: "", file: "" })
    // props.showAlert("Added Successfully", "success");

const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setMenu({ ...menu, category });
}

const onChange = (e) => {
    setMenu({ ...menu, [e.target.name]: e.target.value })
}

return (
    <div className='container my-3'>
        <h3>Add Menu</h3>
        <form className='my-3'>
            <div className="dropdown">
                <button
                    className="btn btn-secondary dropdown-toggle"
                    type="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                    style={{ marginBottom: '5px', marginLeft: '0px' }}
                >
                    {selectedCategory || "Category"} {/* Display the selected category or a default text */}
                </button>
                <ul className="dropdown-menu">
                    {categories.map((category, index) => (
                        <li key={index}>
                            <button
                                className={`dropdown-item ${selectedCategory === category ? 'selected-category' : ''}`}
                                onClick={() => handleCategorySelect(category)}
                                type="button"
                            >
                                {category}
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
            <div className="mb-3">
                <label htmlFor="name" className="form-label">Name</label>
                <input type="text" className="form-control" id="name" name="name" onChange={onChange} value={menu.name} />
            </div>
            <div className="mb-3">
                <label htmlFor="price" className="form-label">Price</label>
                <input type="number" className="form-control" id="price" name="price"
                    value={menu.price} onChange={onChange} />
            </div>
            <div className="mb-3">
                <label htmlFor="description" className="form-label">Description</label>
                <textarea className="form-control" id="description" rows="3" name="description"
                    value={menu.description} onChange={onChange}></textarea>
            </div>
            <div className="mb-3">
                <label htmlFor="file" className="form-label">Select a File</label>
                <input className="form-control" type="text" id="file" name="file"
                    value={menu.file} onChange={onChange} />
            </div>
            <div className="modal-footer">
                <button type="submit" className="btn btn-primary" onClick={handleClick}>Add Menu</button>
            </div>
        </form>
    </div>

)
}

export default AddMenu
