import React, { useState, useContext, useEffect } from 'react'
import "./styles.css"
import menuContext from '../context/menuContext';

const Menu = () => {
    const context = useContext(menuContext);
    const { addMenu } = context;

    const [menu, setMenu] = useState({ name: "", category: "", menus: "", description: "" });
    const [selectedCategory, setSelectedCategory] = useState("");
    const [menuData, setMenuData] = useState([]); // State to store fetched data

    const categories = ["Chinese", "Italian", "Indian", "Beverages"];

    useEffect(() => {
        // Define an async function to fetch data
        async function fetchData() {
            try {
                const response = await fetch('http://localhost:5000/api/menuData/fetchmenudata');
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                const data = await response.json();
                setMenuData(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }

        // Call the async function
        fetchData();
    }, []); // The empty array [] ensures this effect runs once when the component mounts

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
        setMenu({ ...menu, category });
    }

    const handleClick = (e) => {
        e.preventDefault();
        addMenu(menu.name, menu.category, menu.menus, menu.description);
        setMenu({ name: "", category: "", menus: "", description: "" })
    }

    const onChange = (e) => {
        setMenu({ ...menu, [e.target.name]: e.target.value })
    }

    return (
        <div className="container mt-3" style={{ display: "grid" }}>
            <button type="button" className="btn btn-info ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Add New
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered modal-lg">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Add New Category</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="dropdown">
                                <button
                                    className="btn btn-secondary dropdown-toggle"
                                    type="button"
                                    data-bs-toggle="dropdown"
                                    aria-expanded="false"
                                    style={{ marginBottom: '5px', marginLeft: '0px' }}
                                >
                                    {selectedCategory || "Category"} {/* Display the selected category or a default text */}
                                </button>
                                <ul className="dropdown-menu">
                                    {categories.map((category, index) => (
                                        <li key={index}>
                                            <button
                                                className={`dropdown-item ${selectedCategory === category ? 'selected-category' : ''}`}
                                                onClick={() => handleCategorySelect(category)}
                                                type="button"
                                            >
                                                {category}
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="name" className="form-label">Name</label>
                                <input type="text" className="form-control" id="name" name="name" onChange={onChange} value={menu.name} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="menus" className="form-label">Menus</label>
                                <input type="text" className="form-control" id="menus" name="menus"
                                    value={menu.menus} onChange={onChange} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="textarea" className="form-label">Description</label>
                                <textarea className="form-control" id="textarea" rows="3" name="description"
                                    value={menu.description} onChange={onChange}></textarea>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="formFile" className="form-label">Select a File</label>
                                <input className="form-control" type="file" id="formFile" />
                            </div>
                        </div>
                        <div className="modal-footer">
                            {/* <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button> */}
                            <button type="submit" className="btn btn-primary" onClick={handleClick}>Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
            {menuData.map((menuItem, index) => (
                <div className="row" key={index}>
                    <div className="col-md-4">
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">{menuItem.category}</h5>
                                <div className="mb-3">
                                    <label htmlFor="name" className="form-label">Name</label>
                                    <input type="text" className="form-control" id="name" name="name" onChange={onChange} value={menuItem.name} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="category" className="form-label">Category</label>
                                    <input type="text" className="form-control" id="category" name="category" onChange={onChange} value={menuItem.category} />
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="col-md-4">
                        <div className="card" style={{ width: "18rem" }}>
                            <img src="https://img.freepik.com/free-vector/organic-flat-rustic-restaurant-menu-template-with-photo_52683-62703.jpg?w=2000"
                                className="card-img-top" alt="..." />
                            <div className="card-body">
                                <p className="card-text" onChange={onChange}>{menuItem.menus}</p>
                            </div>
                        </div>
                    </div>
                </div>
            ))}

        </div>
    )
}

export default Menu
