import React, { useState, useContext, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import menuContext from '../context/menuContext';

const Category = ({ menuData }) => {
  const navigate = useNavigate();
  const context = useContext(menuContext);
  const { deleteMenu, editMenu } = context;
  const categories = ["Chinese", "Italian", "Indian", "Beverages"];

  const [selectedCategory, setSelectedCategory] = useState("");
  const [menu, setMenu] = useState([]); // State to store fetched data


  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setMenu({ ...menu, ecategory: category });
  };

  const handleLogout = () => {
    // Redirect to the login page
      navigate("/login");
  };

  const ref = useRef(null);
  const refClose = useRef(null);

  const updateMenu = (currentMenu) => {
    if (currentMenu) {
      setMenu({
        ename: currentMenu.name || "",
        ecategory: currentMenu.category || "",
        eprice: currentMenu.price || "",
        edescription: currentMenu.description || "",
        efile: currentMenu.file || "",
      });
      ref.current.click();
    }
  };

  const handleClick = (e) => {
    e.preventDefault();
    editMenu(
      menu.ename,
      menu.ecategory,
      menu.eprice,
      menu.edescription,
      menu.efile
    );
    refClose.current.click();
  };

  const onChange = (e) => {
    setMenu({ ...menu, [e.target.name]: e.target.value });
  };
  
  useEffect(() => {
    // Define an async function to fetch data
    async function fetchData() {
        try {
            const response = await fetch('http://localhost:5000/api/menuData/fetchmenudata');
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setMenu(data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    // Call the async function
    fetchData();
}, []); // The empty array [] ensures this effect runs once when the component mounts


  return (
    <div className='container mt-3' style={{ width: '80%' }}>
      <div className='row'>
        <div className='col-md-3'>
          <div className="card" style={{ width: '180px', height: '100px' }}>
            <div className="card-body">
              <div className="dropdown">

                <strong>Chinese</strong><i className="fa-solid fa-ellipsis-vertical mx-2" data-bs-toggle="dropdown" aria-expanded="false"></i>
                <ul className="dropdown-menu">
                  <li><button type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                    Edit
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menuData) }}></i></button></li>
                  <li><button type="button" className="btn btn-light ml-auto">Delete
                    <i className="fa-solid fa-trash mx-2" onClick={() => { deleteMenu(menuData._id) }}></i></button></li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className='col-md-3'>
          <div className="card" style={{ width: '180px', height: '100px' }}>
            <div className="card-body">
              <div className="dropdown">

                <strong>Italian</strong><i className="fa-solid fa-ellipsis-vertical mx-2" data-bs-toggle="dropdown" aria-expanded="false"></i>
                <ul className="dropdown-menu">
                  <li><button type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                    Edit
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menuData) }}></i></button></li>
                  <li><button type="button" className="btn btn-light ml-auto">Delete<i className="fa-solid fa-trash mx-2" onClick={() => { deleteMenu(menuData._id) }}></i></button></li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className='col-md-3'>
          <div className="card" style={{ width: '180px', height: '100px' }}>
            <div className="card-body">
              <div className="dropdown">

                <strong>Indian</strong><i className="fa-solid fa-ellipsis-vertical mx-2" data-bs-toggle="dropdown" aria-expanded="false"></i>
                <ul className="dropdown-menu">
                  <li><button type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                    Edit
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menuData) }}></i></button></li>
                  <li><button type="button" className="btn btn-light ml-auto">Delete<i className="fa-solid fa-trash mx-2" onClick={() => { deleteMenu(menuData._id) }}></i></button></li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className='col-md-3'>
          <div className="card" style={{ width: '180px', height: '100px' }}>
            <div className="card-body">
              <div className="dropdown">

                <strong>Beverages</strong><i className="fa-solid fa-ellipsis-vertical mx-2" data-bs-toggle="dropdown" aria-expanded="false"></i>
                <ul className="dropdown-menu">
                  <li><button type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                    Edit
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menuData) }}></i></button></li>
                  <li><button type="button" className="btn btn-light ml-auto">Delete<i className="fa-solid fa-trash mx-2" onClick={() => { deleteMenu(menuData._id) }}></i></button></li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">Edit Category</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <div className="dropdown">
                <button
                  className="btn btn-secondary dropdown-toggle"
                  type="button"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                  style={{ marginBottom: '5px', marginLeft: '0px' }}
                >
                  {selectedCategory || "Category"} {/* Display the selected category or a default text */}
                </button>
                <ul className="dropdown-menu">
                  {categories.map((ecategory, index) => (
                    <li key={index}>
                      <button
                        className={`dropdown-item ${selectedCategory === ecategory ? 'selected-category' : ''}`}
                        onClick={() => handleCategorySelect(ecategory)}
                        type="button"
                      >
                        {ecategory}
                      </button>
                    </li>
                  ))}
                </ul>
              </div>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">Name</label>
                <input type="text" className="form-control" id="name" name="name" onChange={onChange} value={menu.ename} />
              </div>
              <div className="mb-3">
                <label htmlFor="price" className="form-label">Price</label>
                <input type="number" className="form-control" id="price" name="price"
                  value={menu.eprice} onChange={onChange} />
              </div>
              <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label">Description</label>
                <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" value={menu.edescription}
                  onChange={onChange}></textarea>
              </div>
              <div className="mb-3">
                <label htmlFor="file" className="form-label">Select a File</label>
                <input className="form-control" type="text" id="file" value={menu.efile} onChange={onChange} />
              </div>
            </div>
            <div className="modal-footer">
              {/* <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button> */}
              <button type="button" className="btn btn-primary" onClick={handleClick}>Save changes</button>
            </div>
          </div>
        </div>
      </div>
      <button type="button" className="btn btn-primary mt-3" onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Category
