import React from 'react';
import { useLocation } from 'react-router-dom';

const BuyNow = () => {
    // Get the selected items from the location state
    const location = useLocation();
    const { selectedItems } = location.state || []; // Use destructuring with a default value to handle potential null or undefined state

    // Function to calculate the total price of selected items
    const calculateTotalPrice = () => {
        return selectedItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    // Function to handle the buy action and display the success message
    const handleBuy = () => {
        // Perform any necessary actions for the buy operation

        // Display a success message
        alert("Order Placed Successfully");
    };

    return (
        <div className="container" style={{ backgroundColor: 'floralwhite', width: '70%', marginBottom: '30px' }}>
            <h2 className="pb-3 fs-4">Selected Items</h2>

            {selectedItems && selectedItems.length > 0 ? (
                <table className="table table-borderless table-hover" style={{ fontFamily: 'Arial', backgroundColor: '#f7f7f7' }}>
                    <thead>
                        <tr>
                            <th className="col-4">Item</th>
                            <th className="col-4">Price</th>
                            <th className="col-4">Quantity</th>
                        </tr>
                    </thead>
                    <tbody>
                        {selectedItems.map((item, index) => (
                            <tr key={index}>
                                <td className="col-4">
                                    <span>{item.name}</span>
                                </td>
                                <td className="col-4">
                                    <span>&#8377;{item.price}</span>
                                </td>
                                <td className="col-4">
                                    <span>{item.quantity}</span>
                                </td>
                            </tr>
                        ))}
                        <tr>
                            <td className="col-4">
                                <strong>Total:</strong>
                            </td>
                            <td className="col-4">
                                <span>&#8377;{calculateTotalPrice()}</span>
                            </td>
                            <td className="col-4">
                                <button className="btn btn-success" onClick={handleBuy}>
                                    Buy
                                </button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            ) : (
                <p>No items selected.</p>
            )}
        </div>
    );
};

export default BuyNow;
