import React from 'react';

const BuyNow = ({ selectedItems}) => {
    
    const onBuy = () => {
        // Perform your buy/order logic here
    
        // Show the "Order Successful" alert message
        alert('Order Successful');
    
        // You can also reset the cart or perform any other necessary actions here
        // Example: reset the cart
        // resetCart();
    
        // You can customize the behavior based on your application's needs
      };

    return (
        <div>
            <h2>Selected Items</h2>
            <ul style={{listStyle: 'none'}}>
                {selectedItems.map((item, index) => (
                    <li key={index}>
                        <div>
                            <img src={item.imageUrl} alt={item.name} />
                        </div>
                        <div>
                            <p>Name: {item.name}</p>
                            <p>Price: &#8377;{item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                            <p>Total: &#8377;{item.price * item.quantity}</p>
                        </div>
                    </li>
                ))}
            </ul>
            <button type="button" onClick={onBuy} className="btn btn-success">
                Buy
            </button>
        </div>
    );
};

export default BuyNow;
