import React, { useState } from "react";
import menuContext from "./menuContext";

const MenuState = (props) => {
    const host = "http://localhost:5000";
    const menuDataInitial = [];
    const [menuData, setMenuData] = useState(menuDataInitial);
    

    const getMenu = async () =>{

        // API CALL
        const response = await fetch(`${host}/api/menuData/fetchmenudata`, {
            method: "GET", 
            headers: {
              "Content-Type": "application/json"
            },
           
          });
          const json = await response.json()
          setMenuData(json);
      }
    
    // Add a menu    
    const addMenu = async (name, category, menus, description) => {
        try {
            const response = await fetch(`${host}/api/menuData/addmenu`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ name, category, menus, description })
            });
    
            if (!response.ok) {
                // Handle HTTP error status codes (e.g., 400 Bad Request, 500 Internal Server Error)
                const errorMessage = await response.text();
                console.error("HTTP Error:", errorMessage);
                // Handle the error as needed, e.g., display an error message to the user.
            } else {
                // Parse the JSON response when successful
                const newMenu = await response.json();
                setMenuData([...menuData, newMenu]);
            }
        } catch (error) {
            // Handle network errors or exceptions
            console.error("Network error:", error);
            // Handle the error as needed.
        }
    };
    
    // Delete a note
  const deletemenu = async (id) =>{
    // API Call
    const response = await fetch(`${host}/api/menuData/deletemenu/${id}`, {
     method: "DELETE", 
     headers: {
       "Content-Type": "application/json",
     },
   });
   const json = response.json(); // parses JSON response into native JavaScript objects
   console.log(json)


  console.log("Deleting the note with id" + id)
  const newMenu = menuData.filter((menu)=>{return menu._id!==id})
  setNotes(newMenu)
}

// Edit a note
const editMenu = async (id, name, category, menus, description) =>{
 // API Call
 const response = await fetch(`${host}/api/menuData/updatemenu/${id}`, {
     method: "PUT", 
     headers: {
       "Content-Type": "application/json",
     },
    
     body: JSON.stringify({name, category, menus, description}) // body data type must match "Content-Type" header
   });
   const json = await response.json(); // parses JSON response into native JavaScript objects
   console.log(json);
  let existingMenu = JSON.parse(JSON.stringify(menuData)) 
 // Logic to edit in client
 for (let index = 0; index < existingMenu.length; index++) {
     const element = newNotes[index];
     if(element._id === id){
       existingMenu[index].name = name;
       existingMenu[index].category = category;
       existingMenu[index].menus = menus;
       existingMenu[index].description = description;
       break;
     }
 }
 setMenuData(existingMenu);
}

    return (
        <menuContext.Provider value={{ menuData, addMenu, getMenu, editMenu, deletemenu }}>
            {props.children}
        </menuContext.Provider>
    );
};

export default MenuState;
