import React, { useState } from 'react';
import { useMenu } from '../context/menuContext';

const AddMenu = () => {
    const { dispatch } = useMenu();

    const [menu, setMenu] = useState({
        name: '',
        category: '',
        price: '',
        description: '',
        file: '',
    });

    const [selectedCategory, setSelectedCategory] = useState('');
    const categories = ['Chinese', 'Italian', 'Indian', 'Beverages'];

    const handleClick = async (e) => {
        e.preventDefault();
        const response = await fetch(`${host}/api/menuData/addmenu`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(menu),
        });

        const addedMenu = await response.json();

        // Dispatch an action to update the state
        dispatch({ type: 'ADD_MENU', payload: addedMenu });

        setMenu({
            name: '',
            category: '',
            price: '',
            description: '',
            file: '',
        });
    };
    
    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
        setMenu({ ...menu, category });
    }

    const onChange = (e) => {
        setMenu({ ...menu, [e.target.name]: e.target.value })
    }
    return (
        <div className='container my-3'>
            <h3>Add Menu</h3>
            <form className='my-3'>
                <div className="dropdown">
                    <button
                        className="btn btn-secondary dropdown-toggle"
                        type="button"
                        data-bs-toggle="dropdown"
                        aria-expanded="false"
                        style={{ marginBottom: '5px', marginLeft: '0px' }}
                    >
                        {selectedCategory || "Category"} {/* Display the selected category or a default text */}
                    </button>
                    <ul className="dropdown-menu">
                        {categories.map((category) => (
                            <li key={category}>
                                <button
                                    className={`dropdown-item ${selectedCategory === category ? 'selected-category' : ''}`}
                                    onClick={() => handleCategorySelect(category)}
                                    type="button"
                                >
                                    {category}
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input type="text" className="form-control" id="name" name="name" onChange={onChange} value={menu.name} />
                </div>
                <div className="mb-3">
                    <label htmlFor="price" className="form-label">Price</label>
                    <input type="number" className="form-control" id="price" name="price"
                        value={menu.price} onChange={onChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="textarea" className="form-label">Description</label>
                    <textarea className="form-control" id="textarea" rows="3" name="description"
                        value={menu.description} onChange={onChange}></textarea>
                </div>
                <div className="mb-3">
                    <label htmlFor="file" className="form-label">Select a File</label>
                    <input className="form-control" type="text" id="file" name="file"
                        value={menu.file} onChange={onChange} />
                </div>
                <div className="modal-footer">
                    <button type="submit" className="btn btn-primary" onClick={handleClick}>Add Menu</button>
                </div>
            </form>
        </div>

    )
}

export default AddMenu
