import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";

const Admin = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        // Redirect to the login page
        navigate('/login');
    };

    // Example restaurant data
    const [restaurantData, setRestaurantData] = useState([]);

    useEffect(async () => {

     const response =  await fetch('/api/auth/restaurants') // Replace with your API endpoint
            .then((response) => response.json())
            .then((data) => {
                setRestaurantData(data); // Update the state with fetched data
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []); // The empty array as the second argument ensures this effect runs once after the component mounts

    return (
        <>
            <h3>Welcome to the Admin Page</h3>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Name</th>
                        <th scope="col">Location</th>
                    </tr>
                </thead>
                <tbody>
                    {restaurantData.map((restaurant) => (
                        <tr key={restaurant.id}>
                            <td>{restaurant.id}</td>
                            <td>{restaurant.name}</td>
                            <td>{restaurant.location}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button type="button" className="btn btn-primary" onClick={handleLogout}>Logout</button>
        </>
    );
};

export default Admin;
