import React, { useState } from "react";
import menuContext from "./menuContext";

const MenuState = (props) => {
    const host = "http://localhost:5000";
    const menuDataInitial = [];
    const [menuData, setMenuData] = useState(menuDataInitial);
    

    const getMenu = async () =>{

        // API CALL
        const response = await fetch(`${host}/api/menuData/fetchmenudata`, {
            method: "GET", 
            headers: {
              "Content-Type": "application/json"
            },
           
          });
          const json = await response.json()
          setMenuData(json);
      }
    
    // Add a menu    
    const addMenu = async (name, category, menus, description ) =>{
        const response = await fetch(`${host}/api/menuData/addmenu`, {
            method: "POST", 
            headers: {
              "Content-Type": "application/json",
            },
           
            body: JSON.stringify({ name, category, menus, description }) // body data type must match "Content-Type" header
          });
            
            const menu = await response.json();
            setMenuData(menuData.concat(menu))
      }

    return (
        <menuContext.Provider value={{ menuData, addMenu, getMenu }}>
            {props.children}
        </menuContext.Provider>
    );
};

export default MenuState;
