import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const BuyNow = () => {
    // Receive the selected items and their details via React Router props
    const { state } = window.history;
    const [cardInfo, setCardInfo] = useState({
        cardNumber: '',
        cardName: '',
        expires: '',
        cvc: '',
    });
    const [orderPlaced, setOrderPlaced] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCardInfo({
            ...cardInfo,
            [name]: value,
        });
    };

    const handleBuyNow = () => {
        // You can handle payment processing here
        // For simplicity, let's assume the payment was successful
        setOrderPlaced(true);
    };
    // Check if there are selected items
    if (state && state.selectedItems) {
        const selectedItems = state.selectedItems;

        // Render the selected items and payment options here
        return (
            <div>
                <h2>Selected Items</h2>
                <ul>
                    {selectedItems.map((item, index) => (
                        <li key={index}>
                            Item: {item.name}, Price: &#8377;{item.price}, Quantity: {item.quantity}
                            <img src={item.imageUrl} alt={item.name} />
                        </li>
                    ))}
                </ul>

                {/* Add payment card options here */}
                <h2>Payment Card</h2>
                <div className="mb-3">
                    <label htmlFor="cardNumber" className="form-label">Card Number</label>
                    <input
                        type="text"
                        className="form-control"
                        id="cardNumber"
                        name="cardNumber"
                        value={cardInfo.cardNumber}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="cardName" className="form-label">Name on Card</label>
                    <input
                        type="text"
                        className="form-control"
                        id="cardName"
                        name="cardName"
                        value={cardInfo.cardName}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="row">
                    <div className="col-6">
                        <div className="mb-3">
                            <label htmlFor="expires" className="form-label">Expires</label>
                            <input
                                type="text"
                                className="form-control"
                                id="expires"
                                name="expires"
                                value={cardInfo.expires}
                                onChange={handleInputChange}
                            />
                        </div>
                    </div>
                    <div className="col-6">
                        <div className="mb-3">
                            <label htmlFor="cvc" className="form-label">CVC</label>
                            <input
                                type="text"
                                className="form-control"
                                id="cvc"
                                name="cvc"
                                value={cardInfo.cvc}
                                onChange={handleInputChange}
                            />
                        </div>
                    </div>
                </div>

                <button type="button" className="btn btn-primary" onClick={handleBuyNow}>
                    Buy Now
                </button>
            </div>
        );
    }

    return (
        <div>
            <p>No items selected.</p>
            <Link to="/">Go back to select items</Link>
        </div>
    );
};

export default BuyNow;
