import './App.css';
import About from './components/About';
import Admin from './components/Admin';
import Category from './components/Category';
import Dashboard from './components/Dashboard';
import Home from './components/Home';
import Login from './components/Login';
import Navbar from './components/Navbar';
import Signup from './components/Signup';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import Alert from './components/Alert';
import { useState } from 'react';
import MenuState from './context/menuData/MenuState';
import PrivateRoute from './components/PrivateRoute';

function App() {
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type
    })
    setTimeout(() => {
      setAlert(null);
    }, 1500)
  }
  const [userIsAdmin, userIsMerchant] = useState(true); // You would replace this with your authentication logic

  return (
    <MenuState>
      <Router>
        <Navbar />
        <Alert alert ={alert}/>
        <div className='container'>
          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route exact path="/about" element={<About />} />
            <Route exact path="/dashboard" element={<Dashboard />} />
            {/* <Route exact path="/category" element={<Category showAlert={showAlert}/>} /> */}
            <Route path="/category" element={<PrivateRoute element={<Category showAlert={showAlert}/>} isMerchant={userIsMerchant} />} />
            <Route exact path="/login" element={<Login />} />
            <Route exact path="/signup" element={<Signup />} />
            {/* <Route exact path="/admin" element={<Admin />} /> */}
            <Route path="/admin" element={<PrivateRoute element={<Admin />} isAdmin={userIsAdmin} />} />
          </Routes>
        </div>
      </Router>
    </MenuState>
  );
}

export default App;
