import './App.css';
import About from './components/About';
import Admin from './components/Admin';
import Category from './components/Category';
import Dashboard from './components/Dashboard';
import Home from './components/Home';
import Login from './components/Login';
import Navbar from './components/Navbar';
import Signup from './components/Signup';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import Alert from './components/Alert';
import { useState } from 'react';
import MenuState from './context/menuData/MenuState';

function App() {
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type
    })
    setTimeout(() => {
      setAlert(null);
    }, 1500)
  }

  const userRole = 'admin'; // Replace with actual user role

  return (
    <MenuState>
      <Router>
        <Navbar />
        <Alert alert={alert} />
        <div className='container'>
          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route exact path="/about" element={<About />} />
            <Route exact path="/dashboard" element={<Dashboard />} />

            {/* Use ProtectedRoute for Category and Admin routes */}
            <Route
              exact
              path="/category"
              element={<Category showAlert={showAlert} />}
              role={userRole}
              authorizedRoles={['admin', 'merchant']}
            />
            <Route
              exact
              path="/admin"
              element={<Admin />}
              role={userRole}
              authorizedRoles={['admin']}
            />

            <Route exact path="/login" element={<Login />} />
            <Route exact path="/signup" element={<Signup />} />
          </Routes>
        </div>
      </Router>
    </MenuState>
  );
}

export default App;
