import React from 'react';
import { Link } from 'react-router-dom';

const BuyNow = ({ selectedItems }) => {

    const handleBuyClick = () => {
        alert('Order Successful');
        window.location.reload(); // This line refreshes the page
    };

    return (
        <div>
            <h2>Selected Items</h2>
            <ul style={{ listStyle: 'none' }}>
                {selectedItems.map((item, index) => (
                    <li key={index}>
                        <div>
                            <img src={item.imageUrl} alt={item.name} />
                        </div>
                        <div>
                            <p>Name: {item.name}</p>
                            <p>Price: &#8377;{item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                            <p>Total: &#8377;{item.price * item.quantity}</p>
                        </div>
                    </li>
                ))}
            </ul>
            {/* <button type="button" onClick={handleBuyClick} className="btn btn-success">
                <Link to="/orderhistory">Buy</Link>
            </button> */}
            <Link
                to={{
                    pathname: '/orderhistory',
                    state: { selectedItems }, // Pass the selected items to the OrderHistory component
                }}
                className="btn btn-success" type="button" onClick={handleBuyClick}
            >
                Buy
            </Link>
        </div>
    );
};

export default BuyNow;
