import React, { useContext } from 'react'
import menuContext from '../context/menuContext';


const CategoryItem = (props) => {
    const context = useContext(menuContext);
    const { deleteMenu } = context;
    const { menu, updateMenu } = props;
    return (
        <div className='col-md-3'>
            <div className="card my-3">
                <div className="card-body">
                    <p className="card-text">{menu.name}</p>
                    <p className="card-text">{menu.category}</p>
                    <p className="card-text">{menu.price}</p>
                    <p className="card-text">{menu.description}</p>
                    <p className="card-text">{menu.file}</p>
                    <i className="fa-solid fa-trash mx-2" onClick={() => { deleteMenu(menu._id); }}></i>
                    <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menu) }}></i>
                </div>
            </div>
        </div>
    )
}

export default CategoryItem
