import React, { useContext } from 'react'
import menuContext from '../context/menuData/menuContext';

const CategoryItem = (props) => {
    const context = useContext(menuContext);
    const { deletemenu } = context;
    const { menu, updateMenu } = props;

    return (
        <div className='col-md-3'>
            <div className="card my-3">
                <div className="card-body">
                    <h5 className="card-title">{menu.category}</h5>
                    <img src={menu.file} className="card-img-top" alt="..." />
                    {/* <p>Price: {menu.price}</p>
                    <p>Description: {menu.description}</p> */}
                    <p><strong style="font-family: Arial; font-size: 16px; color: #333;">Price:</strong> {menu.price}</p>
                    <p><strong style="font-family: Arial; font-size: 16px; color: #333;">Description:</strong> {menu.description}</p>

                    <i className="fa-solid fa-trash mx-2" onClick={() => {
                        deletemenu(menu._id);
                        props.showAlert("Deleted Successfully", "success")
                    }}></i>
                    <button type="button" className="btn btn-light ml-auto" data-bs-toggle="modal" data-bs-target="#exampleModal">
                        <i className="fa-regular fa-pen-to-square mx-2" onClick={() => { updateMenu(menu) }}></i></button>
                </div>
            </div>
        </div>
    )
}

export default CategoryItem
