import React from 'react';

const BuyNow = ({ cart, onPurchase }) => {
    const calculateTotal = () => {
        return cart.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    return (
        <div className="container" style={{ backgroundColor: 'floralwhite', width: '70%', marginBottom: '30px' }}>
            <h2 className="pb-3 fs-4">Order Summary</h2>
            <table className="table table-borderless table-hover" style={{ fontFamily: 'Arial', backgroundColor: '#f7f7f7' }}>
                <thead>
                    <tr>
                        <th className="col-4">Item</th>
                        <th className="col-4">Price</th>
                        <th className="col-4">Quantity</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map((item, index) => (
                        <tr key={index}>
                            <td className="col-4">
                                <span>{item.name}</span>
                            </td>
                            <td className="col-4">
                                <span>&#8377;{item.price}</span>
                            </td>
                            <td className="col-4">
                                <span>{item.quantity}</span>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div>
                <h4>Total: &#8377;{calculateTotal()}</h4>
            </div>
            <button
                type="button"
                className='btn btn-success'
                onClick={onPurchase}
            >
                Purchase
            </button>
        </div>
    );
};

export default BuyNow;
