import React, { useState } from 'react';
import OrderNow from './OrderNow';

const BuyNow = () => {
    const [showBuyNow, setShowBuyNow] = useState(false);

    const handleBuyNow = () => {
        setShowBuyNow(true);
    };

    return (
        <div>
            <OrderNow showBuyNow={showBuyNow} onBuyNow={handleBuyNow} />
            <button onClick={onBuy} className="btn btn-success">
                Buy
            </button>
        </div>
    );
};

export default BuyNow;
