import React, { useState } from 'react';

const BuyNow = ({ selectedItems, onBuyNow, onCancel }) => {
    const [cardInfo, setCardInfo] = useState({
        cardNumber: '',
        nameOnCard: '',
        expires: '',
        cvc: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCardInfo({ ...cardInfo, [name]: value });
    };

    const handleBuyNow = () => {
        // You can perform validation of card information here
        // For simplicity, we are not validating card info in this example
        onBuyNow(selectedItems, cardInfo);
    };

    return (
        <div className="buy-now-container">
            <h2>Selected Items</h2>
            <ul>
                {selectedItems.map((item, index) => (
                    <li key={index}>
                        {item.name} - Price: &#8377;{item.price} - Quantity: {item.quantity}
                    </li>
                ))}
            </ul>
            <h3>Total: &#8377;{selectedItems.reduce((total, item) => total + item.price * item.quantity, 0)}</h3>

            <h2>Card Information</h2>
            <form>
                <div className="form-group">
                    <input
                        type="text"
                        name="cardNumber"
                        placeholder="Card Number"
                        value={cardInfo.cardNumber}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="nameOnCard"
                        placeholder="Name on Card"
                        value={cardInfo.nameOnCard}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="expires"
                        placeholder="Expires (MM/YY)"
                        value={cardInfo.expires}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="cvc"
                        placeholder="CVC"
                        value={cardInfo.cvc}
                        onChange={handleInputChange}
                    />
                </div>
            </form>

            <button onClick={handleBuyNow}>Buy Now</button>
            <button onClick={onCancel}>Cancel</button>
        </div>
    );
};

export default BuyNow;
